import React, { useContext, useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import { ShowContext } from '../src/context/ShowContext';
import Layout from '../src/components/Layout';

const Show = () => {
	const router = useRouter();
	const {showId} = useState(router.query.show);
	const [show, setShow] = useState({});
	console.log(showId);
	useEffect(() => {
		const getShow = async () => {
			const res = await fetch(
				`https://api.tvmaze.com/shows/${router.query.show}`
			);
			const data = await res.json();
			setShow(data);
			console.log(data);
			return data;
		};
		getShow();
	}, [router.query.show]);

	return (
		<Layout>
			<h1>{show.name}</h1>
			<img alt={show.name} />
		</Layout>
	);
};

export default Show;
