import React, { useContext, useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import { ShowContext } from '../src/context/ShowContext';
import Layout from '../src/components/Layout';
import { existsTypeAnnotation } from '@babel/types';

const Show = () => {
	const router = useRouter();
	const [show, setShow] = useState({});

	useEffect(() => {
		const getShow = async () => {
			const showId = await router.query.show;
			const res = await fetch(
				`https://api.tvmaze.com/shows/${router.query.show}`
			);
			const data = await res.json();
			setShow(data);
			console.log(data);
			return data;
		};
		getShow();
	}, [router]);
	const isImage = show.image
		? show.image.medium
		: 'https://static.tvmaze.com/images/no-img/no-img-portrait-text.png';
	const isTime = show.schedule ? show.schedule.time : 'N/A';
	const exists = (con, truValue, other) => (con ? truValue : other);
	return (
		<Layout>
			<h1>{show.name}</h1>
			<img src={isImage} alt={show.name} />
			<h4>Air Date: {show.premiered}</h4>
			<h4>Status: {show.status}</h4>
			<div>
				<h4>
					Schedule <hr />
				</h4>
				<h6>Time - {isTime}</h6>
				<h6>
					Days -{' '}
					{show.schedule &&
						show.schedule.days.map(day =>
							show.schedule.days.length - 1 === show.schedule.days.indexOf(day)
								? day + ''
								: day + ', '
						)}
				</h6>
				<hr />
			</div>
			<div>
				<h4>
					Network <hr />
				</h4>
				<h6>Company: {show.network && show.network.name}</h6>
				<h6>Country: {show.network && show.network.country.code}</h6>
				<hr />
			</div>
		</Layout>
	);
};

export default Show;
