const Show = () => {
	const router = useRouter();
	const { shows } = useContext(ShowContext);

		const getShow = async () => {
			const res = await fetch(`https://api.tvmaze.com/lookup/shows?thetvdb=:id${router}`);
			const data = await res.json();
			//console.log(data);
			console.log(res);
			return data;
		};
	return (
		<Layout>
