import React, { useContext, useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import { ShowContext } from '../src/context/ShowContext';
import Layout from '../src/components/Layout';
import { existsTypeAnnotation } from '@babel/types';

const Show = () => {
	const router = useRouter();
	const [show, setShow] = useState({});

	useEffect(() => {
		const getShow = async () => {
			const showId = await router.query.show;
			const res = await fetch(
				`https://api.tvmaze.com/shows/${router.query.show}`
			);
			const data = await res.json();
			setShow(data);
			console.log(data);
			return data;
		};
		getShow();
	}, [router]);
	const isImage = show.image
		? show.image.medium
		: 'https://static.tvmaze.com/images/no-img/no-img-portrait-text.png';
	const isTime = show.schedule ? show.schedule.time : 'N/A';
	const exists = (con, truValue, other) => (con ? truValue : other);
	return (
		<Layout>
			<h1>{show.name}</h1>
			<img src={isImage} alt={show.name} />
			<h5>Air Date: {show.premiered}</h5>
			<h5>Status: {show.status}</h5>
			<ul>
				<h5>Schedule:</h5>
				<li>Time: {isTime}</li>
				<li>Days: {exists(show.schedule, show.schedule.days, 'N/A')}</li>
			</ul>
		</Layout>
	);
};

export default Show;
